<?php/*--------------------------------------------*//* Email Submited Contact Form                *//*--------------------------------------------*/$post = (!empty($_POST)) ? true : false;if($post){    $to = "your_email@your_domain.com"; // Your e-mail here	$name = stripslashes($_POST['inputName']);	$email = trim($_POST['inputEmail']);	$subject = stripslashes($_POST['inputSubject']);	$message = stripslashes($_POST['inputMessage']);	$sendToYourself = stripslashes($_POST['sendToYourself']);	$phoneNumber = stripslashes($_POST['phoneNumber']);		$headers = (!empty($sendToYourself)) ? 'Reply-To: '.$email . "\r\n" : " ";	$headers .= "From: ".$name."\r\n"             ."Phone Number: ".$phoneNumber."\r\n"             ."X-Mailer: PHP/" . phpversion();			$mail = mail($to, $headers, $subject, $message);	if($mail){		echo 'OK';	}}?>